var audioPlayed = false;

$('#mep_0').ready(function () {
	console.log('START TO PLAY BACKGROUND MUSIC');
  

    window.addEventListener('scroll', function (e) {
        if ($('#mep_0 audio')[0].paused && audioPlayed == false) {
            $('#mep_0 audio')[0].play();
            audioPlayed = true;
       }
    });
  
	setTimeout(function () {
		if (audioPlayed) {
			return;
		}
		$('#mep_0 audio')[0].play();
		audioPlayed = true;
	}, 1000);
});

$('.buttonizer-group').ready(function () {
	console.log('toggles play stat btn ready');
	setTimeout(function () {
		$('.buttonizer-group a').attr("href", "javascript:;")
		iconDom = $('.fa-volume-down');
		$('.buttonizer-group').on('click', function() {
			if ($('#mep_0 audio')[0].paused) {
				$('#mep_0 audio')[0].play();
				audioPlayed = true;
				iconDom.removeClass('fa-volume-off');
				iconDom.addClass('fa-volume-down');
				return;
			}
			iconDom.removeClass('fa-volume-down');
			iconDom.addClass('fa-volume-off');
			$('#mep_0 audio')[0].pause();
		});
	}, 1000);
});

document.addEventListener("WeixinJSBridgeReady", function () { 
	$('#mep_0 audio')[0].play();
}, false);

