var audioPlayed = false;

jQuery('#previewAudio').ready(function () {
	console.log('START TO PLAY BACKGROUND MUSIC');
	setTimeout(function () {
		if (audioPlayed) {
			return;
		}
		jQuery('#previewAudio audio')[0].play();
		audioPlayed = true;
	}, 1000);
});

jQuery('.buttonizer-group').ready(function () {
	console.log('toggles play stat btn ready');
	setTimeout(function () {
		jQuery('.buttonizer-group a').attr("href", "javascript:;")
		iconDom = jQuery('.fa-volume-down');
		jQuery('.buttonizer-group').on('click', function() {
			if (jQuery('#previewAudio audio')[0].paused) {
				jQuery('#previewAudio audio')[0].play();
				audioPlayed = true;
				iconDom.removeClass('fa-volume-off');
				iconDom.addClass('fa-volume-down');
				return;
			}
			iconDom.removeClass('fa-volume-down');
			iconDom.addClass('fa-volume-off');
			jQuery('#previewAudio audio')[0].pause();
		});
	}, 1000);
});

document.addEventListener("WeixinJSBridgeReady", function () { 
	jQuery('#previewAudio audio')[0].play();
}, false);

window.addEventListener('scroll', function (e) {
	if (jQuery('#previewAudio audio')[0].paused && audioPlayed == false) {
   		jQuery('#previewAudio audio')[0].play();
		audioPlayed = true;
   }
})
